function blind_sign($blinded_msg, $n, $e, $d) {
  $blind_sig = gmp_powm($blinded_msg, $d, $n);
  // check
  $m = gmp_powm($blind_sig, $e, $n);
  if (gmp_cmp($m, $blinded_msg) !== 0) {
    print("Signing failure<br>\n");
    return 0;
  }
  return $blind_sig;
}

